ecc_agent_script_include.script:
associateVolume();

function associateVolume() {

	var projectId = this.parameters.get('Project');
	var location = this.parameters.get('Location');
	var volumeName = this.parameters.get('VolumeID');
ms.log('associateVolume called');
ms.log('associateVolume: projectId:' + projectId);
ms.log('associateVolume: location:' + location);
ms.log('associateVolume: volumeName:' + volumeName);

	var volumeRequest = {
		"index": 1,
		"deviceName": volumeName,
		"source" : "https://www.googleapis.com/compute/v1/projects/" + projectId + "/zones/" + location + "/disks/" + volumeName,
		"boot": false,
		"autoDelete": false,
		"interface": "SCSI",
		"mode" : "READ_WRITE"
	};
 ms.log('associateVolume volumeRequest: ' + volumeRequest);

	var gsmd = new GoogleStorageManagedDisk(this.parameters, this.headers);
 	var d = gsmd.associateVolume(volumeRequest);

	return d;
}
