ecc_agent_script_include.script:
createNetwork();

function createNetwork()
{
	var networkName = "" + this.parameters.get('NetworkName');
	var cidrRange = "" + this.parameters.get('CidrBlock');
	var networkRequest = {"name":networkName,"autoCreateSubnetworks":false};
	//var networkRequest = {"name":networkName,"IPv4Range":cidrRange};
	var gcpnw = new GoogleNetworkVirtualNetwork(this.parameters, this.headers);

	var network = gcpnw.createNetwork(networkRequest);

	return network;
}
