sn_cmp_rb_resourcescript.script:
function processResponse(response, cloudServiceAccountId, ldc) {

	var cloudModelString = [];
	var json = new global.JSON();
	var responseObject = json.decode(response);

	for(var i=0; i < responseObject.length; i++)
	{
		var vmInfo = {
			"cmdb_ci_availability_zone": {
				"validator": "virtual_machine_create_update_validator",
				"identification": {
					"cmdb_ci_cloud_service_account": {
						"criterion": {
							"object_id" : cloudServiceAccountId
						}
					},
					"sn_cmp_gcp_google_datacenter": {
						"criterion": {
							"object_id": ldc
						}
					},
					"cmdb_ci_availability_zone": {
						"criterion": {
							"object_id": responseObject[i].availabilityZone
						}
					}
				},
				"attributes": {
					"object_id": responseObject[i].availabilityZone,
					"name": responseObject[i].availabilityZone,
				}
			}
		};
		cloudModelString.push(vmInfo);
	}
	gs.info("Processing Response for List AZ "+global.JSON.stringify(cloudModelString));
	return global.JSON.stringify(cloudModelString);
}

