sn_cmp_rb_resourcescript.script:
function processResponse(response, cloudServiceAccountId, ldc){ 
	var cloudModelString = [];
	var subnetResponse = global.JSON.parse(response);
	
	var networkId = '';      
	var gr = new GlideRecord('cmdb_ci_network');
	gr.addQuery('name',subnetResponse.networkName);
	gr.query();
	if (gr.next()) {
    networkId = gr.getValue('object_id');
	}
	
	var subnetInfo = { 
		"cmdb_ci_cloud_subnet": {
			"validator": "subnet_create_validator", 
			"validator_overrides": {
			}, 
			"identification": {
				"cmdb_ci_cloud_service_account": { 
					"criterion": { 
						"object_id": cloudServiceAccountId 
					}
				}, 
				"sn_cmp_gcp_google_datacenter": { 
					"criterion": { 
						"object_id": ldc 
					} 
				},
				"cmdb_ci_network": { 
					"criterion": { 
						"object_id": networkId
					} 
				},
				"cmdb_ci_cloud_subnet": { 
					"criterion": { 
						"object_id": subnetResponse.subnetId
					} 
				} 
			},
			"attributes": { 
				"object_id": subnetResponse.subnetId,
				"state": "Terminated"
			} 
		}
	};
	cloudModelString.push(subnetInfo);  
	return global.JSON.stringify(cloudModelString); 
}

