sn_cmp_rb_resourcescript.script:
function processResponse(response, cloudServiceAccountId, ldc, correlationId,step, requestorContext) {
	var cloudModelString = [];
		var routerResponse = global.JSON.parse(response);
		var reqContext = global.JSON.parse(requestorContext);
		var routerId = 'Router-'+routerResponse.networkId;
		var router = {
			"cmdb_ci_router": {
				"validator": "router_create_validator",
				"validator_overrides": { },
				"identification": {
					"cmdb_ci_cloud_service_account": {
						"criterion": {
							"object_id": cloudServiceAccountId
						}
					},
					"sn_cmp_gcp_google_datacenter": {
						"criterion": {
							"object_id": ldc
						}
					},
					"cmdb_ci_router": {
						"criterion": {
							"object_id": routerId
						}
					}
				},
				"attributes": {
					"router_id":routerId,
					"object_id": routerId,
					"name":routerId,
					"assigned_to":reqContext.userId,
					"assignment_group":reqContext.groupId,
					"state":"available"
				}
			}
		};
	cloudModelString.push(router);
	return global.JSON.stringify(cloudModelString);
}

