sn_cmp_rb_resourcescript.script:
function processResponse(response, cloudServiceAccountId, ldc) {
	
	var cloudModelString = [];
	var json = new global.JSON();
	var responseObject = json.decode(response);
	gs.info("responseObject: "+responseObject);
	for(var i=0; i < responseObject.length; i++)
	{
		var vmInfo = {
    "cmdb_ci_cloud_subnet": {
      "identification": {
        "cmdb_ci_cloud_service_account": {
          "criterion": {
            "object_id": cloudServiceAccountId
          }
        },
        "sn_cmp_gcp_google_datacenter": {
          "criterion": {
            "object_id": ldc
          }
        },
        "cmdb_ci_cloud_subnet": {
          "criterion": {
            "object_id": responseObject[i].subnetId
          }
        },
		  "cmdb_ci_network": {
					"criterion": {
						"object_id": subnetResponse.networkId
					}
				}
      },
      "attributes": {
        "state": "available",
        "name": responseObject[i].subnetName,
        "object_id": responseObject[i].subnetId,
		"ip_address":responseObject[i].gatewayIP,
		"location":responseObject[i].availabilityZone,
		"cidr":responseObject[i].cidrBlock
      }
	}
  };
		cloudModelString.push(vmInfo);
	}

	gs.info("Output: "+ responseObject[i].subnetName+""+responseObject[i].subnetId+""+responseObject[i].gatewayIP+""+responseObject[i].availabilityZone+""+responseObject[i].cidrBlock);
	return global.JSON.stringify(cloudModelString);
}

